'use strict';

// MODULES //

var tape = require( 'tape' );
var valueOf = require( './../lib/try2valueof.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof valueOf, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if able to extract a string value', function test( t ) {
	t.equal( valueOf( 'a' ), true, 'returns true' );
	t.equal( valueOf( new String( 'a' ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if unable to extract a string value', function test( t ) {
	t.equal( valueOf( {} ), false, 'returns false' );
	t.end();
});
