'use strict';

// MODULES //

var tape = require( 'tape' );
var isTypedArray = require( './../lib' );


// TESTS //


tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isTypedArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'function returns `true` if provided a typed array', function test( t ) {
	var values = [
		new Int8Array( 10 ),
		new Uint8Array( 10 ),
		new Uint8ClampedArray( 10 ),
		new Int16Array( 10 ),
		new Uint16Array( 10 ),
		new Int32Array( 10 ),
		new Uint32Array( 10 ),
		new Float32Array( 10 ),
		new Float64Array( 10 )
	];

	for ( var i = 0; i < values.length; i++ ) {
		t.ok( isTypedArray( values[i] ), values[ i ] );
	}
	t.end();
});

tape( 'function returns `false` if not provided a typed array', function test( t ) {
	var values = [
		'5',
		5,
		NaN,
		null,
		undefined,
		true,
		[],
		{},
		function(){},
		new Buffer( 10 )
	];

	for ( var i = 0; i < values.length; i++ ) {
		t.notOk( isTypedArray( values[i] ), values[ i ] );
	}
	t.end();
});
