'use strict';

// MODULES //

var tape = require( 'tape' );
var isUndefined = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isUndefined, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided `undefined`', function test( t ) {
	var values;
	var bool;
	var foo;
	var i;

	values = [
		foo,
		undefined,
		void 0
	];

	for ( i = 0; i < values.length; i++ ) {
		bool = isUndefined( values[ i ] );
		t.equal( bool, true, 'returns true when provided '+values[ i ] );
	}
	t.end();
});

tape( 'the function returns `false` if not provided `undefined`', function test( t ) {
	var values;
	var i;

	values = [
		'',
		0,
		NaN,
		null,
		false,
		true,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isUndefined( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
