'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isWindows = require( './../lib' );


// TESTS //

tape( 'main export is a boolean', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isWindows, 'boolean', 'main export is a boolean' );
	t.end();
});

tape( 'the main export is `true` if the current process is running on Windows', function test( t ) {
	var isWindows;

	isWindows = proxyquire( './../lib', {
		'@stdlib/utils/platform': 'win32'
	});
	t.equal( isWindows, true, 'is true' );
	t.end();
});

tape( 'the main export is `false` if the current process is not running on Windows', function test( t ) {
	var isWindows;

	isWindows = proxyquire( './../lib', {
		'@stdlib/utils/platform': 'darwin'
	});
	t.equal( isWindows, false, 'is false' );
	t.end();
});
