'use strict';

/**
* Left pads a string such that the padded string has a length of at least `len`.
*
* @module @stdlib/utils/left-pad-string
*
* @example
* var lpad = require( '@stdlib/utils/left-pad-string' );
*
* var str = lpad( 'a', 5 );
* // returns '    a'
*
* str = lpad( 'beep', 10, 'b' );
* // returns 'bbbbbbbeep'
*
* str = lpad( 'boop', 12, 'beep' );
* // returns 'beepbeepboop'
*/

// MODULES //

var lpad = require( './left_pad_string.js' );


// EXPORTS //

module.exports = lpad;
