'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var replace = require( '@stdlib/utils/replace-string' );


// VARIABLES //

var WHITESPACE_REGEX = new RegExp( '^[\t\r\n ]+' );


// LEFT TRIM //

/**
* Trim whitespace characters from beginning of a string.
*
* @param {string} str - input string
* @throws {TypeError} str must be a string primitive
* @returns {string} output string
*
* @example
* var out = ltrim( '   Whitespace   ' );
* // returns 'Whitespace   '
* @example
* var out = ltrim( '\t\t\tTabs\t\t\t' );
* // returns 'Tabs\t\t\t'
* @example
* var out = ltrim( '\n\n\nNew Lines\n\n\n' );
* // returns 'New Lines\n\n\n'
*/
function ltrim( str ) {
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid input argument. First argument must be a string primitive. Value: `' + str + '`.' );
	}
	return replace( str, WHITESPACE_REGEX, '' );
} // end FUNCTION ltrim()


// EXPORTS //

module.exports = ltrim;
