'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var mapFunAsync = require( './../lib' );

function rand( i, next ) {
	var t = randu() * i;
	setTimeout( onTimeout, t );
	function onTimeout() {
		next( null, t );
	}
}

function done( error, out ) {
	if ( error ) {
		throw error;
	}
	console.log( out );
}

mapFunAsync( rand, 10, done );
