'use strict';

/**
* Invoke a function `n` times and return an array of accumulated function return values.
*
* @module @stdlib/utils/map-function-async
*
* @example
* var mapFunAsync = require( '@stdlib/utils/map-function-async' );
*
* function fcn( i, next ) {
*     setTimeout( onTimeout, i );
*     function onTimeout() {
*         next( null, i );
*     }
* }
*
* function done( error, out ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( JSON.stringify( out ) );
*     // => [ 0, 1, 2, 3, 4 ]
* }
*
* mapFunAsync( fcn, 5, done );
*/

// MODULES //

var mapFunAsync = require( './map_function_async.js' );


// EXPORTS //

module.exports = mapFunAsync;
