bench( pkg+'::loop', function benchmark( b ) {
	var arr;
	var i;
	var j;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr = [];
		for ( j = 0; j < 100; j++ ) {
			arr.push( j );
		}
		if ( arr.length === 0 ) {
			b.fail( 'should not be empty' );
		}
	}
	b.toc();
	if ( arr.length === 0 ) {
		b.fail( 'should not be empty' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::this_context', function benchmark( b ) {
	var o;
	var i;

	function fcn( i ) {
		return i;
	}

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		o = mapFun( fcn, 100, {} );
		if ( o.length === 0 ) {
			b.fail( 'should return an output array' );
		}
	}
	b.toc();
	if ( !isArray( o ) ) {
		b.fail( 'should return an output array' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
