'use strict';

var mapKeys = require( './../lib' );

function transform( key, value ) {
	return key + ':' + value;
}

var obj1 = {
	'a': 'beep',
	'b': 'boop',
	'c': 'foo',
	'd': 'bar'
};

var obj2 = mapKeys( obj1, transform );

console.dir( obj2 );
// => { 'a:beep': 'beep', 'b:boop': 'b', 'c:foo': 'foo', 'd:bar': 'bar' }
