'use strict';

var mapValues = require( './../lib' );

function transform( key, value ) {
	return key + ':' + value;
}

var obj1 = {
	'a': 'beep',
	'b': 'boop',
	'c': 'foo',
	'd': 'bar'
};

var obj2 = mapValues( obj1, transform );

console.dir( obj2 );
// => { 'a': 'a:beep', 'b': 'b:boop', 'c': 'c:foo', 'd': 'd:bar' }
