'use strict';

var randu = require( '@stdlib/random/base/randu' );
var floor = require( '@stdlib/math/base/special/floor' );
var memoize = require( './../lib' );

var fcn;
var n;
var v;
var i;

function rand( n ) {
	return n * randu();
}

fcn = memoize( rand );

for ( i = 0; i < 100; i++ ) {
	n = floor( randu() * 5 );
	v = fcn( n );
	console.log( 'rand(%d) = %d', n, v );
}
