'use strict';

/**
* Memoize a function.
*
* @module @stdlib/utils/memoize
*
* @example
* var memoize = require( '@stdlib/utils/memoize' );
*
* function factorial( n ) {
*     var prod;
*     var i;
*     prod = 1;
*     for ( i = n; i > 1; i-- ) {
*         prod *= i;
*     }
*     return prod;
* }
*
* var memoized = memoize( factorial );
*
* var v = memoized( 5 );
* // returns 120
*
* v = memoized( 5 );
* // returns 120
*/

// MODULES //

var memoize = require( './memoize.js' );


// EXPORTS //

module.exports = memoize;
