'use strict';

// MODULES //

var tape = require( 'tape' );
var hasMapSupport = require( '@stdlib/utils/detect-map-support' )();
var hasWeakMapSupport = require( '@stdlib/utils/detect-weakmap-support' )();
var hasSetSupport = require( '@stdlib/utils/detect-set-support' )();
var hasWeakSetSupport = require( '@stdlib/utils/detect-weakset-support' )();
var hasSymbolSupport = require( '@stdlib/utils/detect-symbol-support' )();
var nativeClass = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof nativeClass, 'function', 'export is a function' );
	t.end();
});

tape( 'the function returns a string indicating the specification defined classification of an object (ES5)', function test( t ) {
	var expected;
	var values;
	var v;
	var i;

	values = [
		'a',
		new String( 'a' ),
		5,
		new Number( 5 ),
		NaN,
		true,
		new Boolean( true ),
		false,
		new Boolean( false ),
		undefined,
		null,
		[],
		{},
		function noop(){},
		/./,
		new Date(),
		Math,
		JSON,
		new Error(),
		new TypeError(),
		new SyntaxError(),
		new URIError(),
		new EvalError(),
		new ReferenceError(),
		new RangeError(),
		new Int8Array(),
		new Uint8Array(),
		new Uint8ClampedArray(),
		new Int16Array(),
		new Uint16Array(),
		new Int32Array(),
		new Uint32Array(),
		new Float32Array(),
		new Float64Array(),
		new ArrayBuffer()
	];

	expected = [
		'[object String]',
		'[object String]',
		'[object Number]',
		'[object Number]',
		'[object Number]',
		'[object Boolean]',
		'[object Boolean]',
		'[object Boolean]',
		'[object Boolean]',
		'[object Undefined]',
		'[object Null]',
		'[object Array]',
		'[object Object]',
		'[object Function]',
		'[object RegExp]',
		'[object Date]',
		'[object Math]',
		'[object JSON]',
		'[object Error]',
		'[object Error]',
		'[object Error]',
		'[object Error]',
		'[object Error]',
		'[object Error]',
		'[object Error]',
		'[object Int8Array]',
		'[object Uint8Array]',
		'[object Uint8ClampedArray]',
		'[object Int16Array]',
		'[object Uint16Array]',
		'[object Int32Array]',
		'[object Uint32Array]',
		'[object Float32Array]',
		'[object Float64Array]',
		'[object ArrayBuffer]'
	];

	for ( i = 0; i < values.length; i++ ) {
		v = nativeClass( values[i] );
		t.equal( v, expected[i], 'returns '+expected[i]+' when provided '+values[i] );
	}
	t.end();
});

tape( 'the function supports Map objects (ES2015)', function test( t ) {
	var v;
	if ( hasMapSupport ) {
		v = nativeClass( new Map() );
		t.equal( v, '[object Map]', 'returns [object Map]' );
	}
	t.end();
});

