'use strict';

var randu = require( '@stdlib/random/base/randu' );
var noneByRight = require( './../lib' );

function isPositive( value ) {
	return ( value > 0 );
}

var bool;
var arr;
var i;

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = -randu();
}

bool = noneByRight( arr, isPositive );
console.log( bool );
