'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var noneBy = require( './../lib' );

function isNegative( value ) {
	return ( value < 0 );
}

var bool;
var arr;
var i;

arr = new Array( 100 );
for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = randu();
}

bool = noneBy( arr, isNegative );
console.log( bool );
