'use strict';

// MODULES //

var getOwnPropertyNames = require( '@stdlib/utils/property-names' );
var getPrototypeOf = require( '@stdlib/utils/get-prototype-of' );
var isNonEnumerableProperty = require( '@stdlib/assert/is-nonenumerable-property' );


// FUNCTIONS //

/**
* Returns a boolean indicating if an array contains a provided value.
*
* @private
* @param {Array} arr - array
* @param {*} v - search value
* @returns {boolean} boolean indicating if an array contains a search value
*/
function contains( arr, v ) {
	var i;
	for ( i = 0; i < arr.length; i++ ) {
		if ( arr[ i ] === v ) {
			return true;
		}
	}
	return false;
}


// MAIN //

/**
* Returns an array of an object's own and inherited non-enumerable property names.
*
* @param {*} value - input object
* @returns {Array} a list of own and inherited non-enumerable property names
*
* @example
* var defineProperty = require( '@stdlib/utils/define-property' );
*
* var obj = {};
*
* defineProperty( obj, 'beep', {
*     'configurable': false,
*     'enumerable': true,
*     'writable': false,
*     'value': 'boop'
* });
*
* var keys = nonEnumerableKeysIn( obj );
* // e.g., returns [ 'beep', ... ]
*/
function nonEnumerableKeysIn( value ) {
	var names;
	var obj;
	var tmp;
	var i;

	if ( value === null || value === void 0 ) {
		return [];
	}
	// Cast the value to an object:
	obj = Object( value );

	// Walk the prototype chain collecting all non-enumerable property names...
	names = [];
	do {
		tmp = getOwnPropertyNames( obj );
		for ( i = 0; i < tmp.length; i++ ) {
			if (
				isNonEnumerableProperty( obj, tmp[ i ] ) &&
				contains( names, tmp[ i ] ) === false
			) {
				names.push( tmp[ i ] );
			}
		}
		obj = getPrototypeOf( obj );
	} while ( obj );

	return names;
}


// EXPORTS //

module.exports = nonEnumerableKeysIn;
