'use strict';

/**
* Time in seconds since the epoch.
*
* @module @stdlib/utils/now
*
* @example
* var now = require( '@stdlib/utils/now' );
*
* var ts = now();
* // returns <number>
*/

// MODULES //

var bool = require( './detect.js' );


// MAIN //

var now;
if ( bool ) {
	now = require( './now.js' );
} else {
	now = require( './polyfill.js' );
}


// EXPORTS //

module.exports = now;
