'use strict';

// MODULES //

var nav = require( './navigator.js' );


// MAIN //

/**
* Returns the number of CPUs.
*
* @private
* @returns {PositiveInteger} number of CPUs
*
* @example
* var n = numCPUs();
* // returns <number>
*/
function numCPUs() {
	return nav.hardwareConcurrency || 1;
} // end FUNCTION numCPUs()


// EXPORTS //

module.exports = numCPUs();
