'use strict';

// MODULES //

var os = require( 'os' );


// NUM CPUS //

/**
* Returns the number of CPUs.
*
* @private
* @returns {PositiveInteger} number of CPUs
*
* @example
* var n = numCPUs();
* // returns <number>
*/
function numCPUs() {
	return os.cpus().length;
} // end FUNCTION numCPUs()


// EXPORTS //

module.exports = numCPUs();
