'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isPositiveInteger = require( '@stdlib/utils/is-positive-integer' ).isPrimitive;
var numCPUs = require( './../lib/num_cpus.js' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof numCPUs, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is dependent on the runtime environment (Node.js)', function test( t ) {
	var numCPUs = proxyquire( './../lib/num_cpus.js', {
		'@stdlib/utils/is-browser': false
	});
	t.equal( isPositiveInteger( numCPUs ), true, 'returns a positive integer' );
	t.end();
});

tape( 'the exported value is dependent on the runtime environment (web browser)', function test( t ) {
	var numCPUs = proxyquire( './../lib/num_cpus.js', {
		'@stdlib/utils/is-browser': true
	});
	t.equal( isPositiveInteger( numCPUs ), true, 'returns a positive integer' );
	t.end();
});
