'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var NUM_CPUS = require( './../lib/num_cpus.js' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof NUM_CPUS, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is dependent on the runtime environment (Node.js)', function test( t ) {
	var NUM_CPUS = proxyquire( './../lib/num_cpus.js', {
		'@stdlib/assert/is-browser': false
	});
	t.equal( isPositiveInteger( NUM_CPUS ), true, 'returns a positive integer' );
	t.end();
});

tape( 'the exported value is dependent on the runtime environment (web browser)', function test( t ) {
	var NUM_CPUS = proxyquire( './../lib/num_cpus.js', {
		'@stdlib/assert/is-browser': true
	});
	t.equal( isPositiveInteger( NUM_CPUS ), true, 'returns a positive integer' );
	t.end();
});
