'use strict';

var objectKeys = require( 'object-keys' ).shim();
var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var invert = require( './../lib' );

var arr = new Array( 1000 );
var len = arr.length;
var keys;
var out;
var i;

// Create an array of random integers...
for ( i = 0; i < len; i++ ) {
	arr[ i ] = round( randu()*100 );
}
// Invert the array to determine value frequency...
out = invert( arr );
keys = objectKeys( out );
len = keys.length;
for ( i = 0; i < len; i++ ) {
	out[ i ] = out[ i ].length;
}
console.dir( out );
