'use strict';

/**
* Return an array of an object's own and inherited enumerable property values.
*
* @module @stdlib/utils/object-values-in
*
* @example
* var objectValuesIn = require( '@stdlib/utils/object-values-in' );
*
* function Foo() {
*     this.beep = 'boop';
*     return this;
* }
*
* Foo.prototype.foo = 'bar';
*
* var obj = new Foo();
*
* var values = objectValuesIn( obj );
* // e.g., returns [ 'boop', 'bar' ]
*/

// MODULES //

var objectValuesIn = require( './object_values_in.js' );


// EXPORTS //

module.exports = objectValuesIn;
