'use strict';

/**
* Open a URL.
*
* @module @stdlib/utils/open-url
*
* @example
* var openURL = require( '@stdlib/utils/open-url' );
*
* var proc = openURL( 'https://google.com' );
*/

// MODULES //

var isBrowser = require( '@stdlib/assert/is-browser' );


// MAIN //

var openURL;
if ( isBrowser ) {
	openURL = require( './browser.js' );
} else {
	openURL = require( './open_url.js' );
}


// EXPORTS //

module.exports = openURL;
