'use strict';

/**
* Pad a string.
*
* @module @stdlib/utils/pad-string
*
* @example
* var pad = require( '@stdlib/utils/pad-string' );
*
* var str = pad( 'a', 5 );
* // returns 'a    '
*
* str = pad( 'a', 10, {
*     'lpad': 'b'
* });
* // returns 'bbbbbbbbba'
*
* str = pad( 'a', 12, {
*     'rpad': 'b'
* });
* // returns 'abbbbbbbbbbb'
*
* var opts = {
*     'lpad': 'a',
*     'rpad': 'c'
* };
* str = pad( 'b', 10, opts );
* // returns 'aaaabccccc'
*
* opts = {
*     'lpad': 'a',
*     'rpad': 'c',
*     'centerRight': true
* };
* str = pad( 'b', 10, opts );
* // returns 'aaaaabcccc'
*/

// MODULES //

var pad = require( './pad_string.js' );


// EXPORTS //

module.exports = pad;
