'use strict';

var randu = require( '@stdlib/random/base/randu' );
var floor = require( '@stdlib/math/base/special/floor' );
var papplyRight = require( './../lib' );

var fcn;
var x;
var y;
var z;
var v;
var i;

function add( x, y, z, w, t, s ) {
	return x + y + z + w + t + s;
}

fcn = papplyRight( add, 5, 4, 3 );

for ( i = 0; i < 100; i++ ) {
	x = floor( randu() * 5 );
	y = floor( randu() * 10 );
	z = floor( randu() * 15 );
	v = fcn( x, y, z );
	console.log( '%d+%d+%d+5+4+3 = %d', x, y, z, v );
}
