'use strict';

var randu = require( '@stdlib/random/base/randu' );
var floor = require( '@stdlib/math/base/special/floor' );
var papply = require( './../lib' );

var fcn;
var w;
var t;
var s;
var v;
var i;

function add( x, y, z, w, t, s ) {
	return x + y + z + w + t + s;
}

fcn = papply( add, 5, 4, 3 );

for ( i = 0; i < 100; i++ ) {
	w = floor( randu() * 5 );
	t = floor( randu() * 10 );
	s = floor( randu() * 15 );
	v = fcn( w, t, s );
	console.log( '5+4+3+%d+%d+%d = %d', w, t, s, v );
}
