'use strict';

/**
* Partially apply function arguments.
*
* @module @stdlib/utils/papply
*
* @example
* var papply = require( '@stdlib/utils/papply' );
*
* function add( x, y ) {
*     return x + y;
* }
*
* var add2 = papply( add, 2 );
*
* var sum = add2( 3 );
* // returns 5
*/

// MODULES //

var papply = require( './papply.js' );


// EXPORTS //

module.exports = papply;
