'use strict';

var path = require( 'path' );
var runner = require( './../lib' );

var files;
var opts;
var i;

files = new Array( 10 );
for ( i = 0; i < files.length; i++ ) {
	files[ i ] = path.join( __dirname, 'fixtures/'+i+'.js' );
}

opts = {
	'concurrency': 3,
	'workers': 3
};

runner( files, opts, done );

function done( error ) {
	if ( error ) {
		throw error;
	}
	console.log( 'Done!' );
}
