'use strict';

/**
* Execute scripts in parallel.
*
* @module @stdlib/utils/parallel-runner
*
* @example
* var parallelRunner = require( '@stdlib/utils/parallel-runner' );
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
* var files = [ './a.js', './b.js' ];
* runner( files, done );
*/

// MODULES //

var runner = require( './runner.js' );


// EXPORTS //

module.exports = runner;
