'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );


// TESTS //

tape( 'the file runs a worker script', function test( t ) {
	proxyquire( './../lib/node/worker/index.js', {
		'./worker.js': done
	});
	function done() {
		t.ok( true, __filename );
		t.ok( true, 'runs worker script' );
		t.end();
	}
});
