'use strict';

/**
* Execute scripts in parallel.
*
* @module @stdlib/utils/parallel
*
* @example
* var parallel = require( '@stdlib/utils/parallel' );
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
* var files = [ './a.js', './b.js' ];
* parallel( files, done );
*/

// MODULES //

var parallel = require( './parallel.js' );


// EXPORTS //

module.exports = parallel;
