/* global global */
'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var closeProcess = require( './../lib' );


// FIXTURES //

function database() {
	return {};
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof closeProcess, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function kills all child processes', function test( t ) {
	var closeProcess;
	var count;
	var mock;
	var db;

	mock = {
		'kill': kill
	};

	db = database();
	db.a = mock;
	db.b = mock;

	count = 0;

	closeProcess = proxyquire( './../lib/node/worker/close.js', {
		'./children.js': db,
		'./process.js': {
			'disconnect': disconnect
		}
	});

	closeProcess();

	function kill() {
		count += 1;
	}

	function disconnect() {
		t.equal( count, 2, 'kills child processes' );
		t.deepEqual( db, {}, 'removes children from database' );
		t.end();
	}
});
