'use strict';

var parseJSON = require( './../lib' );

var out = parseJSON( '{"beep":"boop"}' );
console.log( out );
// => {'beep':'boop'}

out = parseJSON( 3.14 );
console.log( out );
// => 3.14

out = parseJSON( true );
console.log( true );
// => true

out = parseJSON( null );
console.log( out );
// => null

out = parseJSON( undefined );
console.log( out instanceof Error );
// => true

out = parseJSON( 'beep' );
console.log( out instanceof Error );
// => true

out = parseJSON( {} );
console.log( out instanceof Error );
// => true

out = parseJSON( [] );
console.log( out instanceof Error );
// => true
