'use strict';

// MODULES //

var tape = require( 'tape' );
var PATH_DELIMITER_POSIX = require( './../lib' );


// TESTS //

tape( 'main export is a string', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof PATH_DELIMITER_POSIX, 'string', 'main export is a string' );
	t.end();
});

tape( 'string is equal to \':\'', function test( t ) {
	t.equal( PATH_DELIMITER_POSIX, ':', 'equals :' );
	t.end();
});
