'use strict';

/**
* POSIX path segment separator.
*
* @module @stdlib/utils/path-sep-posix
*
* @example
* var SEP_POSIX = require( '@stdlib/utils/path-sep-posix' );
*
* var parts = 'foo/bar/baz'.split( SEP_POSIX );
* // returns ['foo','bar','baz']
*/

// SEPARATOR //

/**
* Path segment separator on POSIX platforms.
*
* @constant
* @type {string}
* @default '/'
*/
var SEP_POSIX = '/';


// EXPORTS //

module.exports = SEP_POSIX;
