'use strict';

/**
* Windows path segment separator.
*
* @module @stdlib/utils/path-sep-win32
*
* @example
* var SEP_WIN32 = require( '@stdlib/utils/path-sep-win32' );
*
* var parts = 'foo\\bar\\baz'.split( SEP_WIN32 );
* // returns ['foo','bar','baz']
*/

// SEPARATOR //

/**
* Path segment separator on Windows platforms.
*
* @constant
* @type {string}
* @default '\\'
*/
var SEP_WIN32 = '\\';


// EXPORTS //

module.exports = SEP_WIN32;
