'use strict';

// MODULES //

var tape = require( 'tape' );
var SEP_WIN32 = require( './../lib' );


// TESTS //

tape( 'main export is a string', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof SEP_WIN32, 'string', 'main export is a string' );
	t.end();
});

tape( 'string is equal to \'\\\'', function test( t ) {
	t.equal( SEP_WIN32, '\\', 'equals \\' );
	t.end();
});
