bench( pkg+'::array,multiple', function benchmark( b ) {
	var obj;
	var key;
	var k1;
	var k2;
	var k3;
	var o;
	var i;

	obj = {};
	for ( i = 0; i < 26; i++ ) {
		key = String.fromCharCode( 97+(i%26) );
		obj[ key ] = i;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		k1 = String.fromCharCode( 97+(i%26) );
		k2 = String.fromCharCode( 97+((i+5)%26));
		k3 = String.fromCharCode( 97+((i+12)%26));
		o = pick( obj, [ k1, k2, k3 ] );
		if ( isnan( o[ k1 ] ) ) {
			b.fail( 'should not be NaN' );
		}
	}
	b.toc();
	if ( isnan( o[ k1 ] ) ) {
		b.fail( 'should not be NaN' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

