tape( 'the function returns accepts non-plain objects', function test( t ) {
	var expected;
	var obj1;
	var obj2;

	obj1 = [ 0, 1, 2, 3, 4, 5 ];

	obj2 = pick( obj1, [ '0', '2', '4' ] );

	expected = {
		'0': 0,
		'2': 2,
		'4': 4
	};

	t.deepEqual( obj2, expected, 'returns expected object' );
	t.end();
});
