'use strict';

// MODULES //

var tape = require( 'tape' );
var PLATFORM = require( './../lib' );


// TESTS //

tape( 'main export is a string', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof PLATFORM, 'string', 'main export is a string' );
	t.end();
});

tape( 'should alias `process.platform`', function test( t ) {
	t.equal( PLATFORM, process.platform, 'is alias' );
	t.end();
});
