'use strict';

// MODULES //

var isObject = require( '@stdlib/utils/is-object' );
var isBoolean = require( '@stdlib/utils/is-boolean' ).isPrimitive;


// VALIDATE //

/**
* Validates function options.
*
* @param {Object} opts - destination object
* @param {Object} options - options to validate
* @param {boolean} [options.copy] - boolean indicating whether to return a new data structure
* @returns {(Error|null)} error or null
*
* @example
* var opts = {};
* var options = {
*     'copy': false
* };
* var err = validate( opts, options );
* if ( err ) {
*     throw err;
* }
*/
function validate( opts, options ) {
	if ( !isObject( options ) ) {
		return new TypeError( 'invalid input argument. Options argument must be an object. Value: `' + options + '`.' );
	}
	if ( options.hasOwnProperty( 'copy' ) ) {
		opts.copy = options.copy;
		if ( !isBoolean( opts.copy ) ) {
			return new TypeError( 'invalid option. Copy option must be a boolean primitive. Option: `' + opts.copy + '`.' );
		}
	}
	return null;
} // end FUNCTION validate()


// EXPORTS //

module.exports = validate;
