'use strict';

/**
* Apply a function against an accumulator and each element in a collection and return the accumulated result.
*
* @module @stdlib/utils/reduce-async
*
* @example
* var readFile = require( '@stdlib/fs/read-file' );
* var reduceAsync = require( '@stdlib/utils/reduce-async' );
*
* var files = [
*     './beep.js',
*     './boop.js'
* ];
*
* function done( error, acc ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( acc.count );
* }
*
* function read( acc, file, next ) {
*     var opts = {
*         'encoding': 'utf8'
*     };
*     readFile( file, opts, onFile );
*
*     function onFile( error ) {
*         if ( error ) {
*             return next( null, acc );
*         }
*         acc.count += 1;
*         next( null, acc );
*     }
* }
*
* var acc = {
*     'count': 0
* };
* reduceAsync( files, acc, read, done );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var reduceAsync = require( './reduce.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( reduceAsync, 'factory', factory );


// EXPORTS //

module.exports = reduceAsync;
