bench( pkg+'::loop', function benchmark( b ) {
	var arr;
	var len;
	var o;
	var i;
	var j;

	function sum( acc, v ) {
		return acc + v;
	}

	arr = new Array( 100 );
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		arr[ i ] = i;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr[ 0 ] += 10;
		o = 0;
		for ( j = arr.length-1; j >= 0; j-- ) {
			o = sum( o, arr[ j ], j, arr );
		}
		if ( isnan( o ) ) {
			b.fail( 'should not be NaN' );
		}
	}
	b.toc();
	if ( isnan( o ) ) {
		b.fail( 'should not be NaN' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::loop,inlined', function benchmark( b ) {
	var arr;
	var len;
	var o;
	var i;
	var j;

	arr = new Array( 100 );
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		arr[ i ] = i;
	}
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr[ 0 ] += 10;
		o = 0;
		for ( j = arr.length-1; j >= 0; j-- ) {
			o += arr[ j ];
		}
		if ( isnan( o ) ) {
			b.fail( 'should not be NaN' );
		}
	}
	b.toc();
	if ( isnan( o ) ) {
		b.fail( 'should not be NaN' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

