'use strict';

var regexp = require( './../lib' );

console.log( regexp( '/beep/' ) );
// => /beep/

console.log( regexp( '/[A-Z]*/' ) );
// => /[A-Z]*/

console.log( regexp( '/\\\\\\\//ig' ) );
// => /\\\//ig

console.log( regexp( '/[A-Z]{0,}/' ) );
// => /[A-Z]{0,}/

console.log( regexp( '/^boop$/' ) );
// => /^boop$/

console.log( regexp( '/(?:.*)/' ) );
// => /(?:.*)/

console.log( regexp( '/(?:beep|boop)/' ) );
// => /(?:beep|boop)/

console.log( regexp( '/\\w+/' ) );
// => /\w+/

