'use strict';

/**
* Remove all interpunctuation characters from a string.
*
* @module @stdlib/utils/remove-interpunctuation-string
*
* @example
* var removeInterpunctuation = require( '@stdlib/utils/remove-interpunctuation-string' );
*
* var out = removeInterpunctuation( 'Sun Tzu said: "A leader leads by example not by force."' );
* // returns 'Sun Tzu said A leader leads by example not by force'
*
* var out = removeInterpunctuation( 'Double, double, toil and trouble; Fire burn, and cauldron bubble!' ) );
* // returns 'Double double toil and trouble Fire burn and cauldron bubble'
*/

// MODULES //

var removeInterpunctuation = require( './remove_interpunctuation.js' );


// EXPORTS //

module.exports = removeInterpunctuation;
