'use strict';

/**
* Trim whitespace characters from end of a string.
*
* @module @stdlib/utils/right-trim-string
*
* @example
* var rightTrim = require( '@stdlib/utils/string/right-trim-string' );
*
* var out = rightTrim( '   Whitespace   ' );
* // returns '   Whitespace'
*
* out = rightTrim( '\t\t\tTabs\t\t\t' );
* // returns '\t\t\tTabs'
*
* out = rightTrim( '\n\n\nNew Lines\n\n\n' );
* // returns '\n\n\nNew Lines'
*/

// MODULES //

var rightTrim = require( './right_trim.js' );


// EXPORTS //

module.exports = rightTrim;
