'use strict';

/**
* Execute functions in series, passing the results of one function as arguments to the next function.
*
* @module @stdlib/utils/series-waterfall
*
* @example
* var waterfall = require( '@stdlib/utils/series-waterfall' );
*
* function foo( next ) {
*     next( null, 'beep' );
* }
*
* function bar( str, next ) {
*     console.log( str );
*     // => 'beep'
*
*     next();
* }
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* var fcns = [ foo, bar ];
*
* waterfall( fcns, done );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var waterfall = require( './waterfall.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( waterfall, 'factory', factory );


// EXPORTS //

module.exports = waterfall;
