'use strict';

// MODULES //

var tape = require( 'tape' );
var constructorName = require( '@stdlib/utils/constructor-name' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the returned function throws an error if `copy` is not a boolean primitive', function test( t ) {
	var shuffle;
	var values;
	var i;

	shuffle = factory();

	values = [
		'5',
		5,
		NaN,
		null,
		undefined,
		[],
		{},
		function(){}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			shuffle( [ 1, 2, 3 ], {
				'copy': value
			});
		};
	}
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var shuffle = factory();
	t.equal( typeof shuffle.SEED, 'number', 'has `SEED` property' );
	t.end();
});

tape( 'attached to the returned function is the underlying PRNG', function test( t ) {
	var shuffle = factory();
	t.equal( typeof shuffle.PRNG, 'function', 'has `PRNG` property' );
	t.end();
});

tape( 'the function shuffles the elements of an array', function test( t ) {
	var shuffle = factory( 867 );

	var expected;
	var actual;
	var arr;

	arr = [ 1, 2, 3, 4, 5, 6 ]
	expected = [ 1, 3, 5, 4, 2, 6 ];
	actual = shuffle( arr );

	t.deepEqual( actual, expected, 'deep equal' );
	t.notEqual( arr, actual, 'returns a copy' );
	t.end();
});

tape( 'the function shuffles the elements of a string', function test( t ) {
	var shuffle = factory( 445 );

	var expected;
	var actual;
	var str;

	str = 'hello';
	expected = 'helol';
	actual = shuffle( str );

	t.deepEqual( actual, expected, 'deep equal' );
	t.notEqual( str, actual, 'returns a copy' );
	t.end();
});

tape( 'the function shuffles the elements of a typed array', function test( t ) {
	var shuffle = factory( 867 );

	var expected;
	var actual;
	var arr;

	arr = new Int32Array( [ 1, 2, 3, 4, 5, 6 ] );
	expected = [ 1, 3, 5, 4, 2, 6 ];
	actual = shuffle( arr );

	t.equal( constructorName( actual ), 'Int32Array', 'returns a typed array' );
	t.deepEqual( actual, expected, 'deep equal' );
	t.notEqual( arr, actual, 'returns a copy' );
	t.end();
});

tape( 'the function shuffles the elements of an array (in-place)', function test( t ) {
	var shuffle = factory( 867 );

	var expected;
	var actual;
	var arr;

	arr = [ 1, 2, 3, 4, 5, 6 ]
	expected = [ 1, 3, 5, 4, 2, 6 ];
	actual = shuffle( arr, false );

	t.deepEqual( actual, expected, 'deep equal' );
	t.equal( arr, actual, 'mutates the input array' );
	t.end();
});

tape( 'the function shuffles the elements of a typed array (in-place)', function test( t ) {
	var shuffle = factory( 867 );

	var expected;
	var actual;
	var arr;

	arr = new Int32Array( [ 1, 2, 3, 4, 5, 6 ] );
	expected = [ 1, 3, 5, 4, 2, 6 ];
	actual = shuffle( arr, false );

	t.equal( constructorName( actual ), 'Int32Array', 'returns a typed array' );
	t.deepEqual( actual, expected, 'deep equal' );
	t.equal( arr, actual, 'mutates the input array' );
	t.end();
});
